// SPDX-License-Identifier: MIT
pragma solidity ^0.8.2;

import "../../interfaces/ISeniorPool.sol";
import "../../interfaces/IPoolTokens.sol";
import "../../../alloyx/FIDU.sol";
import "@openzeppelin/contracts/utils/math/SafeMath.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";

/**
 * @title Goldfinch's SeniorPool contract
 * @notice Main entry point for senior LPs (a.k.a. capital providers)
 *  Automatically invests across borrower pools using an adjustable strategy.
 * @author Goldfinch
 */
contract SeniorPool is ISeniorPool {
  using SafeMath for uint256;
  FIDU private fiduCoin;
  IERC20 private usdcCoin;

  event DepositMade(address indexed capitalProvider, uint256 amount, uint256 shares);

  constructor(
    uint256 _sharePrice,
    address _fiduCoinAddress,
    address _usdcCoinAddress
  ) public {
    sharePrice = _sharePrice;
    fiduCoin = FIDU(_fiduCoinAddress);
    usdcCoin = IERC20(_usdcCoinAddress);
  }

  function setSharePrice(uint256 _sharePrice) external {
    sharePrice = _sharePrice;
  }

  /**
   * @notice Deposits `amount` USDC from msg.sender into the SeniorPool, and grants you the
   *  equivalent value of FIDU tokens
   * @param amount The amount of USDC to deposit
   */
  function deposit(uint256 amount) public override returns (uint256 depositShares) {
    require(amount > 0, "Must deposit more than zero");
    // Check if the amount of new shares to be added is within limits
    depositShares = getNumShares(amount);
    emit DepositMade(msg.sender, amount, depositShares);
    bool success = doUSDCTransfer(msg.sender, address(this), amount);
    usdcCoin.approve(address(this), amount);
    require(success, "Failed to transfer for deposit");
    fiduCoin.mint(msg.sender, depositShares);
    return depositShares;
  }

  function doUSDCTransfer(
    address from,
    address to,
    uint256 amount
  ) internal returns (bool) {
    require(to != address(0), "Can't send to zero address");
    return usdcCoin.transferFrom(from, to, amount);
  }

  function depositWithPermit(
    uint256 amount,
    uint256 deadline,
    uint8 v,
    bytes32 r,
    bytes32 s
  ) external override returns (uint256 depositShares) {
    return 0;
  }

  function withdraw(uint256 usdcAmount) external override returns (uint256 amount) {
    return 0;
  }

  function withdrawInFidu(uint256 fiduAmount) external override returns (uint256 amount) {
    require(fiduAmount > 0, "Must withdraw more than zero");
    uint256 usdcAmount = getUSDCAmountFromShares(fiduAmount);
    uint256 withdrawShares = fiduAmount;
    return _withdraw(usdcAmount, withdrawShares);
  }

  function _withdraw(uint256 usdcAmount, uint256 withdrawShares)
    internal
    returns (uint256 userAmount)
  {
    uint256 currentShares = fiduCoin.balanceOf(msg.sender);
    require(
      withdrawShares <= currentShares,
      "Amount requested is greater than what this address owns"
    );

    // Send the amounts
    bool success = doUSDCTransfer(address(this), msg.sender, usdcAmount);

    fiduCoin.burn(msg.sender, withdrawShares);
    return usdcAmount;
  }

  function sweepToCompound() public override {}

  function sweepFromCompound() public override {}

  function invest(ITranchedPool pool) public override {}

  function estimateInvestment(ITranchedPool pool) public view override returns (uint256) {
    return 0;
  }

  function redeem(uint256 tokenId) public override {}

  function writedown(uint256 tokenId) public override {}

  function calculateWritedown(uint256 tokenId)
    public
    view
    override
    returns (uint256 writedownAmount)
  {
    return 0;
  }

  function assets() public view override returns (uint256) {
    return 0;
  }

  /**
   * @notice Converts and USDC amount to FIDU amount
   * @param _amount USDC amount to convert to FIDU
   */
  function getNumShares(uint256 _amount) public view override returns (uint256) {
    return usdcToFidu(_amount).mul(fiduMantissa()).div(sharePrice);
  }

  function usdcToFidu(uint256 amount) internal pure returns (uint256) {
    return amount.mul(fiduMantissa()).div(usdcMantissa());
  }

  function getUSDCAmountFromShares(uint256 fiduAmount) internal view returns (uint256) {
    return fiduToUSDC(fiduAmount.mul(sharePrice).div(fiduMantissa()));
  }

  function fiduToUSDC(uint256 amount) internal pure returns (uint256) {
    return amount.div(fiduMantissa().div(usdcMantissa()));
  }

  function totalShares() internal view returns (uint256) {
    return fiduCoin.totalSupply();
  }

  function fiduMantissa() internal pure returns (uint256) {
    return uint256(10)**uint256(18);
  }

  function usdcMantissa() internal pure returns (uint256) {
    return uint256(10)**uint256(6);
  }
}

contracts/goldfinch/protocol/core/Tranch.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.2;

import "../../interfaces/ITranchedPool.sol";
import "../../interfaces/IPoolTokens.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";

contract TranchedPool is ITranchedPool {
  IPoolTokens private poolToken;
  IERC20 private usdcCoin;

  constructor(address _poolTokenAddress, address _usdcCoinAddress) public {
    poolToken = IPoolTokens(_poolTokenAddress);
    usdcCoin = IERC20(_usdcCoinAddress);
  }

  event DepositMade(
    address indexed owner,
    uint256 indexed tranche,
    uint256 indexed tokenId,
    uint256 amount
  );

  function initialize(
    address _config,
    address _borrower,
    uint256 _juniorFeePercent,
    uint256 _limit,
    uint256 _interestApr,
    uint256 _paymentPeriodInDays,
    uint256 _termInDays,
    uint256 _lateFeeApr,
    uint256 _principalGracePeriodInDays,
    uint256 _fundableAt,
    uint256[] calldata _allowedUIDTypes
  ) public override {}

  function setPoolTokens(address _poolTokens) external {
    poolToken = IPoolTokens(_poolTokens);
  }

  function getTranche(uint256 tranche) external view override returns (TrancheInfo memory) {
    return TrancheInfo(0, 0, 0, 0, 0);
  }

  function pay(uint256 amount) external override {}

  function lockJuniorCapital() external override {}

  function lockPool() external override {}

  function initializeNextSlice(uint256 _fundableAt) external override {}

  function totalJuniorDeposits() external view override returns (uint256) {
    return 0;
  }

  function drawdown(uint256 amount) external override {}

  function setFundableAt(uint256 timestamp) external override {}

  function deposit(uint256 tranche, uint256 amount) external override returns (uint256 tokenId) {
    require(amount > 0, "Must deposit > zero");
    IPoolTokens.MintParams memory params = IPoolTokens.MintParams({
      tranche: tranche,
      principalAmount: amount * 100000
    });
    tokenId = poolToken.mint(params, msg.sender);
    usdcCoin.transferFrom(msg.sender, address(this), amount);
    usdcCoin.approve(address(this), amount);
    emit DepositMade(msg.sender, tranche, tokenId, amount);
    return tokenId;
  }

  function assess() external override {}

  function depositWithPermit(
    uint256 tranche,
    uint256 amount,
    uint256 deadline,
    uint8 v,
    bytes32 r,
    bytes32 s
  ) external override returns (uint256 tokenId) {
    return 0;
  }

  function withdraw(uint256 tokenId, uint256 amount)
    external
    override
    returns (uint256 interestWithdrawn, uint256 principalWithdrawn)
  {
    usdcCoin.transfer(msg.sender, amount);
    return (amount, 0);
  }

  function withdrawMax(uint256 tokenId)
    external
    override
    returns (uint256 interestWithdrawn, uint256 principalWithdrawn)
  {
    return (0, 0);
  }

  function withdrawMultiple(uint256[] calldata tokenIds, uint256[] calldata amounts)
    external
    override
  {}

  /**
   * @notice Determines the amount of interest and principal redeemable by a particular tokenId
   * @param tokenId The token representing the position
   * @return interestRedeemable The interest available to redeem
   * @return principalRedeemable The principal available to redeem
   */
  function availableToWithdraw(uint256 tokenId)
    public
    view
    override
    returns (uint256 interestRedeemable, uint256 principalRedeemable)
  {
    return (tokenId * 1000, tokenId * 10000);
  }
}

contracts/goldfinch/protocol/core/PoolTo.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.2;

import "../../interfaces/ITranchedPool.sol";
import "../../interfaces/IPoolTokens.sol";
import "@openzeppelin/contracts/token/ERC721/ERC721.sol";
import "@openzeppelin/contracts/token/ERC721/extensions/ERC721Enumerable.sol";

/**
 * @title PoolTokens
 * @notice PoolTokens is an ERC721 compliant contract, which can represent
 *  junior tranche or senior tranche shares of any of the borrower pools.
 * @author Goldfinch
 */

contract PoolTokens is ERC721Enumerable, IPoolTokens {
  struct PoolInfo {
    uint256 totalMinted;
    uint256 totalPrincipalRedeemed;
    bool created;
  }

  uint256 lastTokenId = 0;

  // tokenId => tokenInfo
  mapping(uint256 => TokenInfo) public tokens;
  // poolAddress => poolInfo
  mapping(address => PoolInfo) public pools;

  address poolAddress;

  constructor(address _poolAddress) ERC721("PoolTokens", "PTN") {
    poolAddress = _poolAddress;
  }

  /**
   * @notice Called by pool to create a debt position in a particular tranche and amount
   * @param params Struct containing the tranche and the amount
   * @param to The address that should own the position
   * @return tokenId The token ID (auto-incrementing integer across all pools)
   */
  function mint(MintParams calldata params, address to)
    external
    override
    returns (uint256 tokenId)
  {
    return self_mint(params, to, lastTokenId + 1);
  }

  function self_mint(
    MintParams calldata params,
    address to,
    uint256 tokenId
  ) public returns (uint256 tokenIdReturn) {
    tokenId = createToken(params, poolAddress, tokenId);
    _mint(to, tokenId);
    lastTokenId = tokenId;
    return tokenId;
  }

  /**
   * @notice Updates a token to reflect the principal and interest amounts that have been redeemed.
   * @param tokenId The token id to update (must be owned by the pool calling this function)
   * @param principalRedeemed The incremental amount of principal redeemed (cannot be more than principal deposited)
   * @param interestRedeemed The incremental amount of interest redeemed
   */
  function redeem(
    uint256 tokenId,
    uint256 principalRedeemed,
    uint256 interestRedeemed
  ) external override {}

  /**
   * @dev Burns a specific ERC721 token, and removes the data from our mappings
   * @param tokenId uint256 id of the ERC721 token to be burned.
   */
  function burn(uint256 tokenId) external virtual override {}

  function getTokenInfo(uint256 tokenId) external view virtual override returns (TokenInfo memory) {
    return tokens[tokenId];
  }

  function setPoolAddress(address _poolAddress) external {
    poolAddress = _poolAddress;
  }

  /**
   * @notice Called by the GoldfinchFactory to register the pool as a valid pool. Only valid pools can mint/redeem
   * tokens
   * @param newPool The address of the newly created pool
   */
  function onPoolCreated(address newPool) external override {
    pools[newPool].created = true;
  }

  /**
   * @notice Returns a boolean representing whether the spender is the owner or the approved spender of the token
   * @param spender The address to check
   * @param tokenId The token id to check for
   * @return True if approved to redeem/transfer/burn the token, false if not
   */
  function isApprovedOrOwner(address spender, uint256 tokenId)
    external
    view
    override
    returns (bool)
  {
    return _isApprovedOrOwner(spender, tokenId);
  }

  function validPool(address sender) public view virtual override returns (bool) {
    return true;
  }

  function createToken(
    MintParams calldata params,
    address poolAddress,
    uint256 tokenId
  ) internal returns (uint256 tokenIdReturn) {
    tokens[tokenId] = TokenInfo({
      pool: poolAddress,
      tranche: params.tranche,
      principalAmount: params.principalAmount,
      principalRedeemed: 0,
      interestRedeemed: 0
    });
    return tokenId;
  }

  function _getTokenInfo(uint256 tokenId) internal view returns (TokenInfo memory) {
    return tokens[tokenId];
  }
}

